def like_bonjour_monde(client)
    client.search("#bonjour_monde", result_type: "recent").take(1).each do |tweet|
    client.favorite(tweet)
    end
     
end 

def follow_bonjour_monde(client)
    client.search("#bonjour_monde", result_type: "recent").take(1).each do |tweet|
    client.follow(tweet.user)
    end
     
end 

def streaming_twitter
    client = Twitter::Streaming::Client.new do |config|
        config.consumer_key        = ENV["TWITTER_CONSUMER_KEY"]
        config.consumer_secret     = ENV["TWITTER_CONSUMER_SECRET"]
        config.access_token        = ENV["TWITTER_ACCESS_TOKEN"]
        config.access_token_secret = ENV["TWITTER_ACCESS_TOKEN_SECRET"]
        end
       return client

end

def stream_bonjour_monde(streaming_twitter, login_twitter)
    streaming_twitter.filter(track:"#bonjour_monde") do |tweet|
    login_twitter.favorite(tweet)
    login_twitter.follow(tweet.user)
    puts 'ok'
    end
end

